import React, { Component } from "react";
import { Modal, ModalHeader, ModalBody } from "reactstrap";

import { Element } from "react-scroll";

import {
  ProjectContainer,
  ProjectHeader,
  ProjectSubHeader,
  ThumbnailContainer,
  ContentWrapper,
  ButtonWrapper,
} from "./ProjectStyles";

import Button from "../Button/Button";

class Experience extends Component {
  state = {};

  toggleAnchored = () => {
    this.setState({
      modalAnchored: !this.state.modalAnchored,
    });
  };

  toggleCryptoGym = () => {
    this.setState({
      modalCrytpoGym: !this.state.modalCrytpoGym,
    });
  };

  toggleProfJones = () => {
    this.setState({
      modalProfJones: !this.state.modalProfJones,
    });
  };

  toggleBCAD = () => {
    this.setState({
      modalBCAD: !this.state.modalBCAD,
    });
  };

  toggleReactMovies = () => {
    this.setState({
      modalReactMovies: !this.state.modalReactMovies,
    });
  };

  toggleWalletWatcher = () => {
    this.setState({
      modalWalletWatcher: !this.state.modalWalletWatcher,
    });
  };

  render() {
    return (
      <Element name="Projects">
        <ProjectContainer>
          <ContentWrapper>
            <ProjectHeader>Portfolio</ProjectHeader>
            <ProjectSubHeader>
              Click on a project to learn more
            </ProjectSubHeader>
            <ButtonWrapper>
              <a
                href="https://jeremyjones.hackhivebeta.com/"
                target="_blank"
                rel="noopener noreferrer"
                style={{ textDecoration: "none" }}
              >
                <Button
                  name="Full Portfolio"
                  color="#c0c0c0"
                  border="2px solid #c0c0c0"
                  bgColor="#c0c0c0"
                  hoverFontColor="#444649"
                  borderColor="#c0c0c0"
                  width="180px"
                  fontSize="1.2rem"
                />
              </a>
            </ButtonWrapper>
          </ContentWrapper>
        </ProjectContainer>
      </Element>
    );
  }
}

export default Experience;
