import styled from "styled-components";

export const TrainingProgramName = styled.div`
  color: darken(#a2a2a2, 20%);
  display: inline-block;
  font-size: 24px;
  font-weight: 100;
  transition: color 0.3s;

  &:after {
    content: "";
    position: relative;
    left: 50%;
    top: 20px;
    transform: translateX(-50%);
    display: block;
    width: 40px;
    height: 1px;
    background: darken(#a2a2a2, 20%);
    transition: width 0.3s;
  }
`;

export const TrainingHeader = styled.div`
  font-weight: 200;
  font-size: 30px;
  color: #63b7e6;

  text-align: center;
  font-size: 3rem;
  font-weight: 600;
  color: #fffdff;
  opacity: 0.9;
`;

export const TrainingContainer = styled.div`
  font-family: "Raleway", sans-serif;
  padding: 50px 0;
  background: #fff;
  text-align: center;
  word-wrap: break-word;
`;

export const TrainingProgram = styled.div`
  margin: 0;
  display: flex;
  flex-flow: row wrap;
  justify-content: center;
  list-style: none;
`;

export const Program = styled.div`
  padding: 40px;
  width: 45%;
  transition: box-shadow 0.3s;

  &:hover {
    box-shadow: 0 12px 15px 0 rgba(0, 0, 0, 0.24),
      0 17px 50px 0 rgba(0, 0, 0, 0.19);
  }

  &:hover span {
    color: #63b7e6;
  }

  &:hover p {
    color: #555;
  }

  &:hover h3 {
    color: #63b7e6;
  }

  &:hover h3:after {
    width: 25px;
  }

  span {
    font-size: 2.6em;
    display: block;
    transition: color 0.3s;
    color: #a2a2a2;

    &:before,
    &:after {
      position: static;
    }
  }

  p {
    color: #a2a2a2;
    text-align: left;
    text-indent: 1em;
    font-size: 16px;
    line-height: 26px;
    transition: color 0.3s;
  }
`;

export const TrainingIcon = styled.div`
  font-size: 2.6em;
  display: block;
  transition: color 0.3s;
  color: #a2a2a2;

  &:before,
  &:after {
    position: static;
  }
`;
