import React, { Component } from "react";
import PDF from "../../PDF/PDF";
import Button from "../../Button/Button";
import { Element } from "react-scroll";

import {
  AboutContainer,
  ContentWrapper,
  AboutHeader,
  AboutSubHeader,
  IconContainer,
  Icon,
  IconTextWrapper,
  IconText,
  IconHeader,
  BioWrapper,
  BioTextWrapper,
  ProfilePic,
  BioText,
} from "./AboutStyles";

class Certification extends Component {
  state = {
    modal: false,
  };

  togglePDF = () => {
    this.setState({ modal: !this.state.modal });
  };

  render() {
    return (
      <Element name="Certification">
        <AboutContainer>
          <ContentWrapper>
            <AboutSubHeader>My Certifications</AboutSubHeader>
            <IconContainer>
              <IconTextWrapper>
                <Icon className="fas fa-tachometer-alt" />
                <IconHeader>Performant</IconHeader>
                <IconText>
                  Blazing fast load times & lag-free interaction
                </IconText>
              </IconTextWrapper>
              <IconTextWrapper>
                <Icon className="fas fa-mobile-alt" />
                <IconHeader>Responsive</IconHeader>
                <IconText>Sleek & elegant designs for any device size</IconText>
              </IconTextWrapper>
              <IconTextWrapper>
                <Icon className="fas fa-lightbulb" />
                <IconHeader>Intuitive</IconHeader>
                <IconText>User-friendly, dynamic & accessible UI/UX</IconText>
              </IconTextWrapper>
              <IconTextWrapper>
                <Icon className="fas fa-lock" />
                <IconHeader>Secure</IconHeader>
                <IconText>Unhackable systems architecture</IconText>
              </IconTextWrapper>
              <IconTextWrapper>
                <Icon className="fas fa-rocket" />
                <IconHeader>Scalable</IconHeader>
                <IconText>Unlimited user base growth potential</IconText>
              </IconTextWrapper>
            </IconContainer>
          </ContentWrapper>
          <PDF toggle={this.togglePDF} isOpen={this.state.modal} />
        </AboutContainer>
      </Element>
    );
  }
}

export default Certification;
